import{_ as t,o as c,c as b,e as f}from"./app-4ea165c6.js";const e={},p=f('<h1 id="图片格式化" tabindex="-1"><a class="header-anchor" href="#图片格式化" aria-hidden="true">#</a> 图片格式化</h1><p>对于*.hdslb.com/bfs下的图片文件都可以使用以下格式化参数</p><blockquote><p>*.hdslb.com/bfs/*/*.[jpg/png/gif]@{width}w_{high}h_{quality}q.{format}</p></blockquote><table><thead><tr><th>可选参数</th><th>含义</th><th>备注</th></tr></thead><tbody><tr><td>width</td><td>图片最大限制宽度</td><td></td></tr><tr><td>high</td><td>图片最大限制高度</td><td></td></tr><tr><td>quality</td><td>图片质量百分比</td><td>仅限webp</td></tr><tr><td>format</td><td>图片格式</td><td>仅限png/jpg/webp</td></tr></tbody></table><p><strong>示例：</strong></p><p>原始图片</p><p>https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg</p><p><img src="https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg" alt=""></p><p>高度限制为100</p><p>https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@100h</p><p><img src="https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@100h" alt=""></p><p>宽度限制为100</p><p>https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@100w</p><p><img src="https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@100w" alt=""></p><p>转换格式为webp</p><p>https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@.webp</p><p><img src="https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@.webp" alt=""></p><p>转换为webp图片质量为1%</p><p>https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@1q.webp</p><p><img src="https://i1.hdslb.com/bfs/archive/e5fff1472bad1c0c6bcb3004205f9be23b58ffc0.jpg@1q.webp" alt=""></p>',20),d=[p];function h(s,a){return c(),b("div",null,d)}const i=t(e,[["render",h],["__file","picture.html.vue"]]);export{i as default};
